mapper.update:
update dependency
set repository_id = #{repositoryId,jdbcType=INTEGER},
  file_id = #{fileId,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},
  use_count = #{useCount,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, repository_id, file_id, name, use_count
from dependency
where id = #{id,jdbcType=INTEGER}

select id, repository_id, file_id,name, use_count
from dependency
where name = #{name} and file_id= #{fileId}

select id, repository_id, file_id,name, use_count
from dependency
where file_id = #{fileId,jdbcType=INTEGER}
